// Build coaching prompt
const body = $input.item.json.body;
const userInput = body.userInput;
const context = body.context || {};

const prompt = `You are PolymathOS AI - an elite cognitive acceleration coach. Your mission is to provide:
1. Encouraging, specific, and actionable feedback
2. Science-backed learning strategies
3. Personalized recommendations based on the user's learning style
4. Motivation to push boundaries and connect concepts across domains

User Context:
${context.learningStyle ? `Learning Style: ${context.learningStyle}` : ''}
${context.currentDomain ? `Current Domain: ${context.currentDomain}` : ''}
${context.efficiencyMetrics ? `Efficiency: ${JSON.stringify(context.efficiencyMetrics)}` : ''}

User Input: "${userInput}"

Provide coaching feedback that is:
- Specific and actionable
- Encouraging and motivating
- Based on neuroscience and learning science
- Tailored to their learning style
- Focused on accelerating their polymath journey`;

return {
  prompt,
  provider: body.provider || 'nvidia',
  context
};

// Calculate efficiency metrics
const user = $input.item.json;

const totalReviews = user.total_flashcards || 0;
const correctReviews = Math.floor(totalReviews * 0.85); // Assume 85% retention
const retentionRate = totalReviews > 0 ? (correctReviews / totalReviews) * 100 : 95;

const domainDiversity = 0.8; // Default
const consistency = Math.min((user.streak || 0) / 30, 1);
const engagement = Math.min((user.total_study_time || 0) / 1000, 1);
const synthesis = Math.min((user.cross_domain_projects || 0) / 5, 1);

const efficiencyFactors = {
  retention: retentionRate / 100,
  domainDiversity,
  consistency,
  engagement,
  synthesis
};

const efficiencyScore = (Object.values(efficiencyFactors).reduce((a, b) => a + b, 0) / Object.keys(efficiencyFactors).length) * 100;

return {
  efficiencyScore: Math.round(efficiencyScore * 10) / 10,
  retentionRate: Math.round(retentionRate * 10) / 10,
  domainDiversity: Math.round(domainDiversity * 100) / 100,
  learningVelocity: (user.level || 1) * 10 + (user.streak || 0),
  innovationIndex: (user.cross_domain_projects || 0) * 15,
  neuralPlasticity: Math.min(100, (user.deep_work_blocks || 0) * 3),
  efficiencyFactors
};
